const initFlashMessages=()=>{const container=document.getElementById("flash-messages-data");const announcer=document.getElementById("flash-announcer");if(!container)return;const messages=JSON.parse(container.dataset.messages||"[]");messages.forEach((({category:category,message:message})=>{M.toast({html:`<div class="pos-toast pos-toast--${category}" role="alert">\n                <p class="pos-toast-text"><strong>${category.toUpperCase()}:</strong> ${message}</p>\n            </div>`});if(announcer){announcer.textContent=`${category}: ${message}`}}))};document.addEventListener("DOMContentLoaded",initFlashMessages);