function addToInput(button,inputID){const inputElement=document.getElementById(inputID);inputElement.value+=button.value}function clearInput(inputID){const inputElement=document.getElementById(inputID);inputElement.value=""}function deleteLastChar(inputID){const inputElement=document.getElementById(inputID);inputElement.value=inputElement.value.slice(0,-1)}function incrementValue(inputID){const inputField=document.getElementById(inputID);const currentValue=parseInt(inputField.value,10);inputField.value=currentValue+1}function decrementValue(inputID){const inputField=document.getElementById(inputID);const currentValue=parseInt(inputField.value,10);if(currentValue>1){inputField.value=currentValue-1}else{inputField.value=1}}function initFileDropzones(){document.querySelectorAll(".file-dropzone").forEach((zone=>{const input=zone.querySelector(".file-dropzone__input");const preview=zone.querySelector(".file-dropzone__preview");if(!input)return;zone.addEventListener("click",(e=>{if(e.target!==input){input.click()}}));zone.addEventListener("dragenter",(e=>{e.preventDefault();zone.classList.add("file-dropzone--dragover")}));zone.addEventListener("dragover",(e=>{e.preventDefault();zone.classList.add("file-dropzone--dragover")}));zone.addEventListener("dragleave",(e=>{e.preventDefault();zone.classList.remove("file-dropzone--dragover")}));zone.addEventListener("drop",(e=>{e.preventDefault();zone.classList.remove("file-dropzone--dragover");if(e.dataTransfer.files.length>0){input.files=e.dataTransfer.files;updateFilePreview(preview,input.files);input.dispatchEvent(new Event("change",{bubbles:true}))}}));input.addEventListener("change",(()=>{updateFilePreview(preview,input.files)}))}))}function updateFilePreview(previewEl,files){if(!previewEl)return;if(files.length===0){previewEl.textContent=""}else if(files.length===1){previewEl.textContent=files[0].name}else{previewEl.textContent=`${files.length} files selected`}}document.addEventListener("DOMContentLoaded",initFileDropzones);